import React, { useEffect, useState } from "react";
import "./data.json";
export default function PopularRecipe() {
  const [popular, setPopular] = useState([]);
  useEffect(() => {
    getPopular();
  }, []);
  const getPopular = () => {
    // const api = await fetch(
    //   `https://api.spoonacular.com/recipes/random?apiKey=cff2f88292624e4c8617140e75a742cc&number=6`
    // );
    const api = fetch(data.json);
    const data = api.json();

    setPopular(data.recipes);
    // console.log(data);
  };
  // const APIKEY = "943095ffd9b94a21ae71c4a7a61c6acc";
  return (
    <>
      {popular.map((recipes) => {
        return (
          <div className="col">
            <div className="card">
              <img
                src={recipes.image}
                className="card-img-top"
                alt="Hollywood Sign on The Hill"
              />
              <div className="card-body">
                <h5 className="card-title">{recipes.title}</h5>
                {/* <p className="card-text">{recipes.summary}</p> */}
              </div>
            </div>
          </div>
        );
      })}
    </>
  );
}
