import React, { useEffect, useState } from "react";
import { Splide, SplideSlide } from "@splidejs/react-splide";
// Default theme
import "@splidejs/react-splide/css";
import mi from "../../assets/HeroImage.jpg";

export default function PopularRecipe() {
  const [popular, setPopular] = useState([]);
  useEffect(() => {
    getPopular();
  }, []);
  const getPopular = async () => {
    let query = "paneer";
    const appID = "c0076e1a";
    const APIkey = "c114954e889aaecca1d5dd73241a6dd1";
    const response = await fetch(
      `https://api.edamam.com/search?q=${query}&app_id=${appID}&app_key=${APIkey}`
    );
    const data = await response.json();
    setPopular(data.hits);
    console.log(data);

    // const api = await fetch(
    //   `https://api.spoonacular.com/recipes/random?apiKey=cff2f88292624e4c8617140e75a742cc&number=6`
    // );
    // const data = await api.json();
    // setPopular(data.recipes);
    // console.log(data);
  };
  // const APIKEY = "943095ffd9b94a21ae71c4a7a61c6acc";
  return (
    <>
      <Splide
        options={{
          perPage: 3,
          type: "loop",
          gap: "2rem",
          pagination: false,
          arrows: false,
        }}
      >
        {popular.map((recipes) => {
          return (
            <SplideSlide>
              <div className="recipeCard ">
                <div className="imageBg">
                  {" "}
                  <img
                    src={recipes.recipe.image}
                    // src={mi}
                    className="card-img-top myPopularImage"
                    alt="Hollywood Sign on The Hill"
                  />
                </div>

                <div className="card-body">
                  <h3 className="card-title">{recipes.recipe.label}</h3>
                  {/* <h3>Recipes Title </h3> */}
                  <ul className="list-group list-group-flush">
                    <li className="list-group-item">
                      Calories - {recipes.recipe.calories}
                    </li>
                    <li className="list-group-item">
                      Meal Type - {recipes.recipe.mealType}
                    </li>
                    <li className="list-group-item">
                      Dish Type - {recipes.recipe.dishType}
                    </li>
                  </ul>
                </div>
              </div>
            </SplideSlide>
          );
        })}
      </Splide>
    </>
  );
}
